import React, { useEffect, useState } from "react";
import { point } from "@turf/helpers";
import booleanPointInPolygon from "@turf/boolean-point-in-polygon";

function AGEBInfo({ selectPosition }) {
  const [geoData, setGeoData] = useState(null);
  const [selectedPolygon, setSelectedPolygon] = useState(null);

  useEffect(() => {
    fetch("/agebs.geojson")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setGeoData(data);
      })
      .catch((error) => {
        console.error("Error fetching the GeoJSON data:", error);
      });
  }, []);

  useEffect(() => {
    if (geoData && selectPosition && selectPosition.lat && selectPosition.lon) {
      const lat = parseFloat(selectPosition.lat);
      const lon = parseFloat(selectPosition.lon);

      if (!isNaN(lat) && !isNaN(lon)) {
        try {
          const pt = point([lon, lat]);

          const containingPolygon = geoData.features.find((feature) => {
            return booleanPointInPolygon(pt, feature);
          });

          setSelectedPolygon(containingPolygon);
          console.log("features:", containingPolygon);
        } catch (error) {
          console.error("Error in polygon selection:", error);
        }
      } else {
        console.error("Invalid coordinates:", selectPosition);
      }
    }
  }, [geoData, selectPosition]);

  if (!selectedPolygon) {
    return <div>Cargando...</div>; // Return loading state while fetching or processing data
  }

  const {
    CVEGEO,
    salud_count,
    deportivos_count,
    infraestructura_count,
    comercios_count,
    educacion_count,
    pp_count,
    guarderias,
  } = selectedPolygon.properties || {};

  return (
    <div className="ageb-info">
      <h2>Neighborhood Information</h2>
      <p>
        CVEGEO: {CVEGEO} <br />
        Salud Count: {salud_count} <br />
        Deportivos Count: {deportivos_count} <br />
        Infraestructura Count: {infraestructura_count} <br />
        Comercios Count: {comercios_count} <br />
        Educacion Count: {educacion_count} <br />
        PP Count: {pp_count} <br />
        Guarderias: {guarderias} <br />
      </p>
      {/* Add more neighborhood information here */}
    </div>
  );
}

export default AGEBInfo;
