import React, { useEffect, useState } from "react";

function AGEBInfo({ ageb }) {
  const [agebData, setAgebData] = useState(null);

  useEffect(() => {
    if (ageb) {
      fetch("/agebs.geojson")
        .then((response) => {
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          return response.json();
        })
        .then((data) => {
          const selectedAGEB = data.features.find(
            (feature) => feature.properties.CVEGEO === ageb.properties.CVEGEO
          );
          if (selectedAGEB) {
            setAgebData(selectedAGEB);
          } else {
            throw new Error(
              `AGEB with CVEGEO ${ageb.properties.CVEGEO} not found in GeoJSON data`
            );
          }
        })
        .catch((error) => {
          console.error("Error fetching or processing GeoJSON data:", error);
        });
    }
  }, [ageb]);

  // Ensure ageb and agebData are truthy before rendering
  if (!ageb || !agebData) {
    return <div>Cargando...</div>; // Display loading state
  }

  return (
    <div className="ageb-info">
      <h2>Neighborhood Information</h2>
      <p>
        CVEGEO: {agebData.properties.CVEGEO} <br />
        Salud Count: {agebData.properties.salud_count} <br />
        Deportivos Count: {agebData.properties.deportivos_count} <br />
        Infraestructura Count: {agebData.properties.infraestructura_count}{" "}
        <br />
        Comercios Count: {agebData.properties.comercios_count} <br />
        Educacion Count: {agebData.properties.educacion_count} <br />
        PP Count: {agebData.properties.pp_count} <br />
        Guarderias: {agebData.properties.guarderias} <br />
      </p>
      {/* Add more neighborhood information here */}
    </div>
  );
}

export default AGEBInfo;
