import React, { useEffect, useState } from "react";
import "./App.css";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { MapContainer, TileLayer, Marker, Popup, GeoJSON } from "react-leaflet";
import { Icon } from "leaflet";
import { useMap } from "react-leaflet/hooks";
import AGEBInfo from "./AGEBInfo"; // Assuming AGEBInfo.js is in the same directory

const icon = L.icon({
  iconUrl: "./placeholder.png",
  iconSize: [38, 38],
});

const position = [19.432608, -99.133209];

function ResetCenterView(props) {
  const { selectPosition } = props;
  const map = useMap();

  useEffect(() => {
    if (selectPosition) {
      map.setView(
        L.latLng(selectPosition?.lat, selectPosition?.lon),
        map.getZoom(),
        {
          animate: true,
        }
      );
    }
  }, [selectPosition]);

  return null;
}

function Maps(props) {
  const { selectPosition } = props;
  const [geoData, setGeoData] = useState(null);
  const [containingPolygon, setContainingPolygon] = useState(null); // State to hold the selected polygon

  useEffect(() => {
    fetch("/agebs.geojson")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setGeoData(data);
      })
      .catch((error) => {
        console.error("Error fetching the GeoJSON data:", error);
      });
  }, []);

  useEffect(() => {
    if (geoData && selectPosition) {
      const point = L.latLng(selectPosition.lat, selectPosition.lon);

      // Find the polygon that contains the point
      const containingPolygon = geoData.features.find((feature) => {
        const layer = L.geoJSON(feature);
        return layer.getBounds().contains(point);
      });

      setContainingPolygon(containingPolygon); // Set the selected polygon
    }
  }, [geoData, selectPosition]);

  const customIcon = new Icon({
    iconUrl: require("./assets/marker.png"),
    iconSize: [38, 38],
  });

  return (
    <div className="map-container">
      <MapContainer center={position} zoom={13}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {selectPosition && (
          <Marker
            position={[selectPosition.lat, selectPosition.lon]}
            icon={customIcon}
          >
            <Popup>
              A pretty CSS3 popup. <br /> Easily customizable.
            </Popup>
          </Marker>
        )}
        <ResetCenterView selectPosition={selectPosition} />
        {geoData && <GeoJSON data={geoData} />}
        {containingPolygon && <AGEBInfo ageb={containingPolygon} />}{" "}
        {/* Pass containingPolygon as ageb prop */}
      </MapContainer>
    </div>
  );
}

export default Maps;
