import React, { useState } from "react";
import "./App.css";
import AGEBInfo from "./AGEBInfo";

const NOMINATIM_BASE_URL = "https://nominatim.openstreetmap.org/search?";

function AddressBox(props) {
  const { setSelectPosition } = props;
  const [searchText, setSearchText] = useState("");
  const [listPlace, setListPlace] = useState([]);
  const [isSelected, setIsSelected] = useState(false); // State to track selection
  const [selectedPlace, setSelectedPlace] = useState(null); // State to hold selected place

  const handleSearch = () => {
    // Reset state when a new search is initiated
    setIsSelected(false);
    setSelectedPlace(null);

    // Search
    const params = {
      q: searchText,
      format: "json",
      addressdetails: 1,
      polygon_geojson: 0,
    };
    const queryString = new URLSearchParams(params).toString();
    const requestOptions = {
      method: "GET",
      redirect: "follow",
    };
    fetch(`${NOMINATIM_BASE_URL}${queryString}`, requestOptions)
      .then((response) => response.text())
      .then((result) => {
        console.log(JSON.parse(result));
        setListPlace(JSON.parse(result));
      })
      .catch((err) => console.log("err: ", err));
  };

  const handleSelect = (item) => {
    setSelectPosition(item);
    setIsSelected(true); // Update state to indicate an item is selected
    setSelectedPlace(item); // Set the selected place
    setListPlace([]); // Clear the list
  };

  return (
    <div className="box-container">
      <div className="search-container">
        <input
          type="text"
          placeholder="Ingresa tu direcciÃ³n..."
          className="search-bar"
          value={searchText}
          onChange={(event) => setSearchText(event.target.value)}
        />
        <button className="search-button" type="submit" onClick={handleSearch}>
          <i className="fa fa-search"></i>
        </button>
      </div>

      {!isSelected && (
        <div>
          <ul>
            {listPlace.map((item) => (
              <div
                key={item?.place_id}
                className="list-item"
                onClick={() => handleSelect(item)}
              >
                <i className="fa fa-map-marker"></i>
                <span className="item-text">{item?.display_name}</span>
                <div className="divider"></div>
              </div>
            ))}
          </ul>
        </div>
      )}

      {isSelected && selectedPlace && <AGEBInfo neighborhood={selectedPlace} />}
    </div>
  );
}

export default AddressBox;
