import React, { useState, useEffect } from "react";

function AGEBInfo({ ageb }) {
  const [agebData, setAgebData] = useState(null);

  useEffect(() => {
    if (ageb) {
      fetch("/agebs.geojson")
        .then((response) => {
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          return response.json();
        })
        .then((data) => {
          const selectedAGEB = data.features.find(
            (feature) => feature.properties.CVEGEO === ageb.properties.CVEGEO
          );
          if (selectedAGEB) {
            setAgebData(selectedAGEB.properties);
          } else {
            throw new Error(
              `AGEB with CVEGEO ${ageb.properties.CVEGEO} not found in GeoJSON data`
            );
          }
        })
        .catch((error) => {
          console.error("Error fetching or processing GeoJSON data:", error);
        });
    }
  }, [ageb]);

  // Return loading state if agebData is not set yet
  if (!agebData) {
    return <div>Loading ageb data...</div>;
  }

  return (
    <div className="ageb-info">
      <h2>Neighborhood Information</h2>
      <p>
        Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod
        tempor incididunt ut labore et dolore magna aliqua.
      </p>
      {/* Add more neighborhood information here */}
    </div>
  );
}

export default AGEBInfo;
