import React, { useEffect, useState } from "react";
import "./App.css";
import "leaflet/dist/leaflet.css";
import L from "leaflet";
import { MapContainer, TileLayer, Marker, Popup, GeoJSON } from "react-leaflet";
import { Icon } from "leaflet";
import { useMap } from "react-leaflet/hooks";

const icon = L.icon({
  iconUrl: "./placeholder.png",
  iconSize: [38, 38],
});

const position = [19.432608, -99.133209];

function ResetCenterView(props) {
  const { selectPosition } = props;
  const map = useMap();

  useEffect(() => {
    if (selectPosition) {
      map.setView(
        L.latLng(selectPosition?.lat, selectPosition?.lon),
        map.getZoom(),
        {
          animate: true,
        }
      );
    }
  }, [selectPosition, map]);

  return null;
}

function Maps(props) {
  const { selectPosition } = props;
  const [geoData, setGeoData] = useState(null);
  const [selectedPolygon, setSelectedPolygon] = useState(null);
  const locationSelection = [selectPosition?.lat, selectPosition?.lon];

  useEffect(() => {
    fetch("/agebs.geojson")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setGeoData(data);
      })
      .catch((error) => {
        console.error("Error fetching the GeoJSON data:", error);
      });
  }, []);

  useEffect(() => {
    if (geoData && selectPosition && selectPosition.lat && selectPosition.lon) {
      const lat = parseFloat(selectPosition.lat);
      const lon = parseFloat(selectPosition.lon);

      if (!isNaN(lat) && !isNaN(lon)) {
        try {
          const pt = point([lon, lat]);
          const bufferedPoint = buffer(pt, 0.1, { units: "kilometers" });

          const containingPolygon = geoData.features.find((feature) => {
            return booleanIntersects(bufferedPoint, feature);
          });

          setSelectedPolygon(containingPolygon);
        } catch (error) {
          console.error("Error in polygon selection:", error);
        }
      } else {
        console.error("Invalid coordinates:", selectPosition);
      }
    }
  }, [geoData, selectPosition]);

  const customIcon = new Icon({
    iconUrl: require("./assets/marker.png"),
    iconSize: [38, 38],
  });

  const styleFunction = (feature) => {
    if (feature === selectedPolygon) {
      return { color: "red", fillColor: "red", fillOpacity: 0.3 };
    }
    return { color: "transparent", fillColor: "transparent" };
  };

  return (
    <div className="map-container">
      <MapContainer center={position} zoom={13}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        {selectPosition && (
          <Marker position={locationSelection} icon={customIcon}>
            <Popup>
              A pretty CSS3 popup. <br /> Easily customizable.
            </Popup>
          </Marker>
        )}
        <ResetCenterView selectPosition={selectPosition} />
        {selectedPolygon && (
          <GeoJSON
            key={JSON.stringify(selectedPolygon)}
            data={selectedPolygon}
            style={{ color: "blue", fillColor: "blue", fillOpacity: 0.3 }}
          >
            <Popup>This is the AGEB containing the selected point.</Popup>
          </GeoJSON>
        )}
      </MapContainer>
    </div>
  );
}

export default Maps;
