import React, { useEffect, useState } from "react";

function AGEBInfo({ ageb }) {
  const {
    CVEGEO,
    salud_count,
    deportivos_count,
    infraestructura_count,
    comercios_count,
    educacion_count,
    pp_count,
    guarderias,
  } = ageb.properties;

  const [neighborhoodData, setNeighborhoodData] = useState(null);

  useEffect(() => {
    fetch("/agebs.geojson")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        // Find the specific ageb within the geojson data
        const selectedAGEB = data.features.find(
          (feature) => feature.properties.CVEGEO === CVEGEO
        );
        setNeighborhoodData(selectedAGEB);
      })
      .catch((error) => {
        console.error("Error fetching the GeoJSON data:", error);
      });
  }, [CVEGEO]);

  if (!neighborhoodData) {
    return <div>Loading...</div>; // Placeholder for loading state
  }

  return (
    <div className="ageb-info">
      <h2>Neighborhood Information</h2>
      <p>
        CVEGEO: {neighborhoodData.properties.CVEGEO} <br />
        Salud Count: {neighborhoodData.properties.salud_count} <br />
        Deportivos Count: {neighborhoodData.properties.deportivos_count} <br />
        Infraestructura Count:{" "}
        {neighborhoodData.properties.infraestructura_count} <br />
        Comercios Count: {neighborhoodData.properties.comercios_count} <br />
        Educacion Count: {neighborhoodData.properties.educacion_count} <br />
        PP Count: {neighborhoodData.properties.pp_count} <br />
        Guarderias: {neighborhoodData.properties.guarderias} <br />
      </p>
      {/* Add more neighborhood information here */}
    </div>
  );
}

export default AGEBInfo;
