import React, { useEffect, useState } from "react";
import "./App.css";
import "leaflet/dist/leaflet.css";
import L from "leaflet";

import { MapContainer } from "react-leaflet/MapContainer";
import { TileLayer } from "react-leaflet/TileLayer";
import { Marker, Popup } from "react-leaflet";
import { Icon, divIcon } from "leaflet";
import MarkerClusterGroup from "react-leaflet-cluster";
import { useMap } from "react-leaflet/hooks";
import { GeoSearchControl, OpenStreetMapProvider } from "leaflet-geosearch";

const icon = L.icon({
  iconUrl: "./placeholder.png",
  iconSize: [38, 38],
});

const position = [19.432608, -99.133209];

function ResetCenterView(props) {
  const { selectPosition } = props;
  const map = useMap();

  useEffect(() => {
    if (selectPosition) {
      map.setView(
        L.latLng(selectPosition?.lat, selectPosition?.lon),
        map.getZoom(),
        {
          animate: true,
        }
      );
    }
  }, [selectPosition]);

  return null;
}

function Maps(props) {
  const { selectPosition } = props;
  const [geoData, setGeoData] = useState(null);
  const [selectedPolygon, setSelectedPolygon] = useState(null);
  const locationSelection = [selectPosition?.lat, selectPosition?.lon];

  useEffect(() => {
    fetch("/agebs.geojson")
      .then((response) => {
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        return response.json();
      })
      .then((data) => {
        setGeoData(data);
      })
      .catch((error) => {
        console.error("Error fetching the GeoJSON data:", error);
      });
  }, []);

  useEffect(() => {
    if (geoData && selectPosition) {
      const point = L.latLng(selectPosition.lat, selectPosition.lon);

      // Find the polygon that contains the point
      const containingPolygon = geoData.features.find((feature) => {
        const layer = L.geoJSON(feature);
        return layer.getBounds().contains(point);
      });

      setSelectedPolygon(containingPolygon);
    }
  }, [geoData, selectPosition]);

  // Test Markers
  const markers = [
    {
      geocode: [19.50387, -99.20958],
      popUp: "Test 1",
    },
    {
      geocode: [19.42881, -99.194679],
      popUp: "Test 2",
    },
    {
      geocode: [19.428512573242188, -99.16178131103516],
      popUp: "Test 3",
    },
  ];

  const customIcon = new Icon({
    iconUrl: require("./assets/marker.png"),
    iconSize: [38, 38],
  });

  return (
    <div className="map-container">
      <MapContainer center={position} zoom={13}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {selectPosition && (
          <Marker position={locationSelection} icon={customIcon}>
            <Popup>
              A pretty CSS3 popup. <br /> Easily customizable.
            </Popup>
          </Marker>
        )}
        <ResetCenterView selectPosition={selectPosition} />

        {/* <MarkerClusterGroup chunkedLoading>
          {markers.map((marker) => (
            <Marker position={marker.geocode} icon={customIcon}>
              <Popup>
                <h2>{marker.popUp}</h2>
              </Popup>
            </Marker>
          ))}
        </MarkerClusterGroup> */}
      </MapContainer>
    </div>
  );
}

export default Maps;
